                var sessionKey = "jCdFs2HMx+A9Dr9lhMJKxA==";
                var compareSignature = "2d65ebea7c7f500bfb874b71569a591047452d38";

                var signature = EncryptHelper.GetSignature(rawData, sessionKey);
